"use client";
import { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import * as THREE from "three";
import { motion } from "framer-motion";

function WavePlane() {
  const meshRef = useRef<THREE.Mesh>(null);
  const geo = useRef<THREE.PlaneGeometry>(null);
  useFrame((state) => {
    if (!geo.current) return;
    const pos = geo.current.attributes.position;
    const time = state.clock.elapsedTime;
    for (let i = 0; i < pos.count; i++) {
      const x = pos.getX(i);
      const y = pos.getY(i);
      pos.setZ(i, Math.sin(x * 0.8 + time) * 0.3 + Math.sin(y * 0.6 + time * 0.8) * 0.3);
    }
    pos.needsUpdate = true;
  });
  return (
    <mesh ref={meshRef} rotation={[-Math.PI / 3, 0, 0]} position={[0, -1, 0]}>
      <planeGeometry ref={geo} args={[20, 20, 60, 60]} />
      <meshStandardMaterial color="#3b0764" wireframe />
    </mesh>
  );
}

