"use client";
import { motion, useMotionValue, useSpring, useTransform } from "framer-motion";
import { useRef, MouseEvent } from "react";

// Magnetic button - cursor attracts button
function MagneticButton({ children }: { children: React.ReactNode }) {
  const ref = useRef<HTMLButtonElement>(null);
  const x = useMotionValue(0);
  const y = useMotionValue(0);
  const sx = useSpring(x, { stiffness: 150, damping: 15 });
  const sy = useSpring(y, { stiffness: 150, damping: 15 });

  const handleMove = (e: MouseEvent) => {
    if (!ref.current) return;
    const rect = ref.current.getBoundingClientRect();
    const cx = rect.left + rect.width / 2;
    const cy = rect.top + rect.height / 2;
    x.set((e.clientX - cx) * 0.35);
    y.set((e.clientY - cy) * 0.35);
  };

  return (
    <motion.button ref={ref} style={{ x: sx, y: sy }}
      onMouseMove={handleMove} onMouseLeave={() => { x.set(0); y.set(0); }}
      className="px-8 py-4 rounded-full bg-white text-black font-semibold hover:bg-white/90 transition-colors"
    >
      {children}
    </motion.button>
  );
}

// Gradient border button
