"use client";
import { motion, useMotionValue, useSpring, useTransform } from "framer-motion";
import { MouseEvent, useRef } from "react";

function TiltCard({ title, description, accent }: { title: string; description: string; accent: string }) {
  const ref = useRef<HTMLDivElement>(null);
  const x = useMotionValue(0);
  const y = useMotionValue(0);
  const rx = useSpring(useTransform(y, [-0.5, 0.5], ["-15deg", "15deg"]), { stiffness: 200, damping: 20 });
  const ry = useSpring(useTransform(x, [-0.5, 0.5], ["-15deg", "15deg"]), { stiffness: 200, damping: 20 });

  const handleMove = (e: MouseEvent) => {
    if (!ref.current) return;
    const rect = ref.current.getBoundingClientRect();
    x.set((e.clientX - rect.left) / rect.width - 0.5);
    y.set((e.clientY - rect.top) / rect.height - 0.5);
  };

  return (
    <motion.div ref={ref} onMouseMove={handleMove} onMouseLeave={() => { x.set(0); y.set(0); }}
      style={{ rotateX: rx, rotateY: ry, transformStyle: "preserve-3d" }}
      className="w-64 h-80 rounded-2xl bg-[#111] border border-white/10 p-6 cursor-pointer"
      whileHover={{ z: 20 }}
    >
      <div className="w-12 h-12 rounded-xl mb-4" style={{ backgroundColor: accent + "20", border: `1px solid ${accent}30` }}>
        <div className="w-full h-full flex items-center justify-center text-xl">✦</div>
      </div>
      <h3 className="text-white font-semibold text-lg mb-2" style={{ transform: "translateZ(20px)" }}>{title}</h3>
      <p className="text-white/40 text-sm leading-relaxed" style={{ transform: "translateZ(10px)" }}>{description}</p>
      <div className="absolute bottom-6 left-6 right-6 h-px" style={{ background: `linear-gradient(to right, ${accent}40, transparent)` }} />
    </motion.div>
  );
}

