import { motion } from "framer-motion";

function AnimatedText({ text, className }: { text: string; className?: string }) {
  const words = text.split(" ");
  const container = {
    hidden: {},
    visible: { transition: { staggerChildren: 0.06 } }
  };
  const child = {
    hidden: { opacity: 0, y: "110%", skewY: 5 },
    visible: { opacity: 1, y: "0%", skewY: 0, transition: { duration: 0.7, ease: [0.16, 1, 0.3, 1] } }
  };
  return (
    <motion.p variants={container} initial="hidden" animate="visible" className={`flex flex-wrap gap-x-[0.3em] ${className}`}>
      {words.map((word, i) => (
        <span key={i} className="overflow-hidden inline-block">
          <motion.span variants={child} className="inline-block">{word}</motion.span>
        </span>
      ))}
    </motion.p>
  );
}

function CharStagger({ text, className }: { text: string; className?: string }) {
  const container = {
    hidden: {},
    visible: { transition: { staggerChildren: 0.04 } }
  };
  const child = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.5, ease: [0.16, 1, 0.3, 1] } }
  };
  return (
    <motion.h2 variants={container} initial="hidden" animate="visible" className={className}>
      {text.split("").map((char, i) => (
        <motion.span key={i} variants={child} className="inline-block">
          {char === " " ? " " : char}
        </motion.span>
      ))}
    </motion.h2>
  );
}

