export default function SlideTransition() {
  const [[page, dir], setPage] = useState([0, 0]);

  const navigate = (newPage: number) => {
    setPage([newPage, newPage > page ? 1 : -1]);
  };

  const current = pages[page];

  return (
    <div className="min-h-screen bg-[#0c0c0c] flex flex-col">
      <nav className="flex gap-1 p-4 z-10">
        {pages.map((p, i) => (
          <button key={p.id} onClick={() => navigate(i)}
            className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${i === page ? "text-white bg-white/10" : "text-white/40 hover:text-white"}`}
          >
            {p.label}
          </button>
        ))}
      </nav>
      <div className="flex-1 relative overflow-hidden">
        <AnimatePresence initial={false} custom={dir} mode="popLayout">
          <motion.div key={page} custom={dir} variants={variants}
            initial="enter" animate="center" exit="exit"
            transition={{ duration: 0.5, ease: [0.16, 1, 0.3, 1] }}
            className="absolute inset-0 flex items-center justify-center"
          >
            <div className="text-center">
              <div className="w-24 h-24 rounded-full mx-auto mb-8" style={{ backgroundColor: current.color + "20", border: `2px solid ${current.color}40` }} />
