        </AnimatePresence>
      </div>
      <div className="flex gap-2 mb-8">
        {steps.map((_, i) => (
          <div key={i} className={`w-2 h-2 rounded-full transition-all duration-300 ${i === step ? "bg-white w-6" : "bg-white/20"}`} />
        ))}
      </div>
      <div className="flex gap-3">
        <button onClick={() => setStep(s => Math.max(0, s - 1))} disabled={step === 0}
          className="px-6 py-3 rounded-lg border border-white/10 text-white/60 hover:text-white disabled:opacity-30 transition-all">
          Back
        </button>
        <button onClick={() => setStep(s => Math.min(steps.length - 1, s + 1))} disabled={step === steps.length - 1}
          className="px-6 py-3 rounded-lg bg-white text-black font-semibold disabled:opacity-30 hover:bg-white/90 transition-all">
          Next
        </button>
      </div>
    </div>
  );
}

