"use client";
import { useRef, useEffect } from "react";
import * as THREE from "three";

const vertexShader = `
  varying vec2 vUv;
  void main() {
    vUv = uv;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
  }
`;

const fragmentShader = `
  uniform float uTime;
  uniform vec2 uResolution;
  varying vec2 vUv;

  vec3 palette(float t) {
    vec3 a = vec3(0.2, 0.5, 0.8);
    vec3 b = vec3(0.3, 0.4, 0.3);
    vec3 c = vec3(1.0, 1.0, 0.8);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(6.28318 * (c * t + d));
  }

  float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
  }

  float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);
    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
  }

  void main() {
    vec2 uv = vUv;
    float t = uTime * 0.3;

    float aurora = 0.0;
    for (int i = 0; i < 4; i++) {
      float fi = float(i);
      vec2 noiseUv = vec2(uv.x * 2.0 + t * 0.1 + fi * 0.5, uv.y * 3.0 + fi * 0.3);
      float n = noise(noiseUv) * 0.5 + noise(noiseUv * 2.0) * 0.25 + noise(noiseUv * 4.0) * 0.125;
      float band = smoothstep(0.3, 0.7, uv.y + n * 0.4 - fi * 0.15);
      band *= (1.0 - smoothstep(0.5, 0.9, uv.y + n * 0.3 - fi * 0.15));
      aurora += band * 0.4;
    }

    vec3 color = palette(aurora + t * 0.1) * aurora;
    color += vec3(0.02, 0.05, 0.08);

    gl_FragColor = vec4(color, 1.0);
  }
`;

export default function AuroraBackground() {
  const mountRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!mountRef.current) return;
    const W = mountRef.current.clientWidth;
    const H = mountRef.current.clientHeight;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(W, H);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    mountRef.current.appendChild(renderer.domElement);

    const scene = new THREE.Scene();
    const camera = new THREE.OrthographicCamera(-1, 1, 1, -1, 0, 1);

    const material = new THREE.ShaderMaterial({
      vertexShader,
      fragmentShader,
      uniforms: {
        uTime: { value: 0 },
        uResolution: { value: new THREE.Vector2(W, H) }
      }
    });

    const mesh = new THREE.Mesh(new THREE.PlaneGeometry(2, 2), material);
    scene.add(mesh);

    let animId: number;
    const animate = () => {
      animId = requestAnimationFrame(animate);
      material.uniforms.uTime.value += 0.01;
      renderer.render(scene, camera);
    };
    animate();

    const onResize = () => {
      if (!mountRef.current) return;
      const w = mountRef.current.clientWidth;
      const h = mountRef.current.clientHeight;
      renderer.setSize(w, h);
      material.uniforms.uResolution.value.set(w, h);
    };
    window.addEventListener("resize", onResize);

    return () => {
      cancelAnimationFrame(animId);
      window.removeEventListener("resize", onResize);
      renderer.dispose();
      material.dispose();
      mountRef.current?.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <div ref={mountRef} className="absolute inset-0" />
      <div className="relative z-10 flex items-center justify-center h-full text-center">
        <div>
          <h1 className="font-['Fraunces'] text-7xl md:text-9xl font-bold text-white leading-none mb-4">
            Aurora
          </h1>
          <p className="text-white/60 text-xl">Real-time GLSL shader</p>
        </div>
      </div>
    </div>
  );
}

"use client";
import { useRef, useEffect } from "react";
import * as THREE from "three";

const fragmentShader = `
  uniform float uTime;
  uniform vec2 uResolution;
  varying vec2 vUv;

  void main() {
    vec2 uv = vUv;
    float t = uTime * 0.5;

    vec3 col1 = vec3(0.48, 0.22, 0.93);
    vec3 col2 = vec3(0.06, 0.72, 0.93);
    vec3 col3 = vec3(0.93, 0.24, 0.55);

    float n1 = sin(uv.x * 3.0 + t) * cos(uv.y * 2.0 + t * 0.7);
    float n2 = sin(uv.x * 2.0 - t * 0.6) * sin(uv.y * 3.0 + t * 0.4);
    float n3 = cos(uv.x * 4.0 + t * 0.3) * cos(uv.y * 1.5 - t * 0.8);

    float blend1 = (n1 + 1.0) * 0.5;
    float blend2 = (n2 + 1.0) * 0.5;

    vec3 color = mix(mix(col1, col2, blend1), col3, blend2 * 0.5);
    color = pow(color, vec3(0.8));

    gl_FragColor = vec4(color, 1.0);
  }
`;

const vertexShader = `varying vec2 vUv; void main() { vUv = uv; gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0); }`;

export default function ChromaFlowBg() {
  const ref = useRef<HTMLDivElement>(null);
  useEffect(() => {
    if (!ref.current) return;
    const W = ref.current.clientWidth, H = ref.current.clientHeight;
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(W, H);
    ref.current.appendChild(renderer.domElement);
    const scene = new THREE.Scene();
    const camera = new THREE.OrthographicCamera(-1,1,1,-1,0,1);
    const mat = new THREE.ShaderMaterial({ vertexShader, fragmentShader, uniforms: { uTime: {value:0}, uResolution: {value: new THREE.Vector2(W,H)} } });
    scene.add(new THREE.Mesh(new THREE.PlaneGeometry(2,2), mat));
    let id: number;
    const tick = () => { id = requestAnimationFrame(tick); mat.uniforms.uTime.value += 0.016; renderer.render(scene, camera); };
    tick();
    return () => { cancelAnimationFrame(id); renderer.dispose(); ref.current?.removeChild(renderer.domElement); };
  }, []);
  return (
    <div className="relative w-full h-screen">
      <div ref={ref} className="absolute inset-0" />
      <div className="absolute inset-0 bg-black/20" />
      <div className="relative z-10 flex items-center justify-center h-full">
        <h1 className="font-['Syne'] text-8xl font-black text-white drop-shadow-2xl">CHROMA</h1>
      </div>
    </div>
  );
}

