"use client";
import { useRef, useState, KeyboardEvent, ChangeEvent } from "react";
import { motion } from "framer-motion";

export default function OTPInput() {
  const [otp, setOtp] = useState(Array(6).fill(""));
  const refs = useRef<(HTMLInputElement | null)[]>([]);
  const [verified, setVerified] = useState(false);

  const handleChange = (i: number, e: ChangeEvent<HTMLInputElement>) => {
    const val = e.target.value.replace(/\D/, "").slice(-1);
    const newOtp = [...otp];
    newOtp[i] = val;
    setOtp(newOtp);
    if (val && i < 5) refs.current[i + 1]?.focus();
    if (newOtp.every(v => v !== "") && newOtp.join("").length === 6) setVerified(true);
  };

  const handleKey = (i: number, e: KeyboardEvent) => {
    if (e.key === "Backspace" && !otp[i] && i > 0) refs.current[i - 1]?.focus();
  };

  return (
    <div className="min-h-screen bg-[#0c0c0c] flex items-center justify-center">
      <div className="w-full max-w-sm text-center">
        <h2 className="text-white text-2xl font-semibold mb-2">Enter verification code</h2>
        <p className="text-white/40 text-sm mb-8">We sent a 6-digit code to your email</p>
        <div className="flex gap-3 justify-center mb-8">
          {otp.map((digit, i) => (
            <motion.input
              key={i}
              ref={el => { refs.current[i] = el; }}
              value={digit}
              onChange={e => handleChange(i, e)}
              onKeyDown={e => handleKey(i, e)}
              maxLength={1}
              animate={{ borderColor: digit ? "#7c3aed" : "rgba(255,255,255,0.1)", scale: digit ? 1.05 : 1 }}
              className="w-12 h-14 text-center text-white text-xl font-bold bg-white/5 border rounded-xl outline-none focus:border-violet-500 transition-colors"
            />
          ))}
        </div>
        <AnimatePresence>
          {verified && (
            <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0 }}
              className="flex items-center gap-2 justify-center text-emerald-400 text-sm mb-4">
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" /></svg>
              Code verified successfully!
            </motion.div>
          )}
        </AnimatePresence>
        <button className="text-white/30 text-sm hover:text-white/60 transition-colors">Resend code</button>
      </div>
    </div>
  );
}

