import { useRef, useEffect } from "react";
import * as THREE from "three";

export default function EarthGlobe() {
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!ref.current) return;
    const W = ref.current.clientWidth, H = ref.current.clientHeight;

    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    renderer.setSize(W, H);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    ref.current.appendChild(renderer.domElement);

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(45, W / H, 0.1, 100);
    camera.position.set(0, 0, 3);

    // Globe
    const geo = new THREE.SphereGeometry(1, 64, 64);
    const mat = new THREE.MeshPhongMaterial({
      color: 0x1a3a5c,
      emissive: 0x112244,
      specular: 0x4488aa,
      shininess: 40,
      wireframe: false,
    });
    const globe = new THREE.Mesh(geo, mat);
    scene.add(globe);

    // Wireframe overlay
    const wireMat = new THREE.MeshBasicMaterial({ color: 0x2255aa, wireframe: true, transparent: true, opacity: 0.08 });
    scene.add(new THREE.Mesh(new THREE.SphereGeometry(1.001, 32, 32), wireMat));

    // Atmosphere glow
    const atmMat = new THREE.MeshPhongMaterial({
      color: 0x4488ff,
      transparent: true,
      opacity: 0.08,
      side: THREE.BackSide,
    });
    scene.add(new THREE.Mesh(new THREE.SphereGeometry(1.15, 32, 32), atmMat));

    // Markers
    const markerPositions = [
      { lat: 40.7, lng: -74.0 },   // NYC
      { lat: 51.5, lng: -0.1 },    // London
      { lat: 35.7, lng: 139.7 },   // Tokyo
      { lat: -33.9, lng: 151.2 },  // Sydney
      { lat: 48.9, lng: 2.35 },    // Paris
    ];

    markerPositions.forEach(({ lat, lng }) => {
      const phi = (90 - lat) * (Math.PI / 180);
      const theta = (lng + 180) * (Math.PI / 180);
      const marker = new THREE.Mesh(
        new THREE.SphereGeometry(0.02, 8, 8),
        new THREE.MeshBasicMaterial({ color: 0x00d9ff })
      );
      marker.position.set(
        -Math.sin(phi) * Math.cos(theta),
        Math.cos(phi),
        Math.sin(phi) * Math.sin(theta)
      );
      globe.add(marker);
    });

    // Lights
    scene.add(new THREE.AmbientLight(0x334466, 2));
    const dirLight = new THREE.DirectionalLight(0x8899ff, 3);
    dirLight.position.set(5, 3, 5);
    scene.add(dirLight);

    // Mouse drag rotation
    let isDragging = false, lastX = 0, lastY = 0;
    const onDown = (e: MouseEvent) => { isDragging = true; lastX = e.clientX; lastY = e.clientY; };
    const onUp = () => { isDragging = false; };
    const onMove = (e: MouseEvent) => {
      if (!isDragging) return;
      globe.rotation.y += (e.clientX - lastX) * 0.005;
      globe.rotation.x += (e.clientY - lastY) * 0.003;
      lastX = e.clientX; lastY = e.clientY;
    };
    renderer.domElement.addEventListener("mousedown", onDown);
    window.addEventListener("mouseup", onUp);
    window.addEventListener("mousemove", onMove);

    let id: number;
    const animate = () => {
      id = requestAnimationFrame(animate);
      if (!isDragging) globe.rotation.y += 0.002;
      renderer.render(scene, camera);
    };
    animate();

    const onResize = () => {
      if (!ref.current) return;
      const w = ref.current.clientWidth, h = ref.current.clientHeight;
      camera.aspect = w / h; camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    };
    window.addEventListener("resize", onResize);

    return () => {
      cancelAnimationFrame(id);
      window.removeEventListener("mouseup", onUp);
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("resize", onResize);
      renderer.dispose();
      ref.current?.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div className="w-full h-screen bg-[#020610] flex items-center justify-center">
      <div className="relative">
        <div ref={ref} className="w-[500px] h-[500px] cursor-grab active:cursor-grabbing" />
        <p className="text-center text-white/20 text-xs mt-4">Drag to rotate · Click markers to explore</p>
      </div>
    </div>
  );
}

"use client";
import { useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import * as THREE from "three";
import { useMemo } from "react";

function ParticleGlobe() {
  const points = useRef<THREE.Points>(null);
  const count = 5000;

  const positions = useMemo(() => {
    const pos = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      const phi = Math.acos(1 - 2 * Math.random());
      const theta = Math.random() * Math.PI * 2;
      pos[i * 3] = Math.sin(phi) * Math.cos(theta);
      pos[i * 3 + 1] = Math.cos(phi);
      pos[i * 3 + 2] = Math.sin(phi) * Math.sin(theta);
    }
    return pos;
  }, []);

  useFrame(state => {
    if (points.current) points.current.rotation.y = state.clock.elapsedTime * 0.1;
  });

  return (
    <points ref={points}>
      <bufferGeometry>
        <bufferAttribute attach="attributes-position" args={[positions, 3]} />
      </bufferGeometry>
      <pointsMaterial size={0.008} color="#00d9ff" transparent opacity={0.7} sizeAttenuation />
    </points>
  );
}

