"use client";
import { motion, AnimatePresence } from "framer-motion";
import { useState, useCallback } from "react";

type Toast = { id: number; type: "success"|"error"|"warning"|"info"; message: string };

const icons = { success: "✓", error: "✕", warning: "⚠", info: "ℹ" };
const colors = {
  success: "bg-emerald-500/10 border-emerald-500/20 text-emerald-400",
  error: "bg-red-500/10 border-red-500/20 text-red-400",
  warning: "bg-amber-500/10 border-amber-500/20 text-amber-400",
  info: "bg-blue-500/10 border-blue-500/20 text-blue-400"
};

let nextId = 0;

export default function ToastSystem() {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const add = useCallback((type: Toast["type"], message: string) => {
    const id = nextId++;
    setToasts(t => [...t, { id, type, message }]);
    setTimeout(() => setToasts(t => t.filter(x => x.id !== id)), 4000);
  }, []);

  const remove = (id: number) => setToasts(t => t.filter(x => x.id !== id));

  return (
    <div className="min-h-screen bg-[#0c0c0c] flex flex-col items-center justify-center gap-3">
      <div className="flex gap-3 flex-wrap justify-center">
        {(["success","error","warning","info"] as const).map(type => (
          <button key={type} onClick={() => add(type, `This is a ${type} message!`)}
            className="px-4 py-2 rounded-lg bg-white/5 border border-white/10 text-white/70 text-sm hover:bg-white/8 transition-colors capitalize">
            {type}
          </button>
        ))}
      </div>
      <div className="fixed bottom-6 right-6 flex flex-col gap-2 z-50">
        <AnimatePresence initial={false}>
          {toasts.map(toast => (
            <motion.div key={toast.id}
              initial={{ opacity: 0, x: 50, scale: 0.9 }}
              animate={{ opacity: 1, x: 0, scale: 1 }}
              exit={{ opacity: 0, x: 50, scale: 0.9 }}
              transition={{ duration: 0.3, ease: [0.16,1,0.3,1] }}
              className={`flex items-center gap-3 px-4 py-3 rounded-xl border backdrop-blur-sm min-w-[280px] ${colors[toast.type]}`}
            >
              <span className="font-bold text-sm">{icons[toast.type]}</span>
              <span className="text-sm flex-1 text-white/80">{toast.message}</span>
              <button onClick={() => remove(toast.id)} className="text-white/30 hover:text-white/60 transition-colors">✕</button>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>
    </div>
  );
}

